import React from 'react'
import { useState } from 'react';
import './contactus.css'

const ContactUs = () => {
  const[name,setname]=useState("");
  const[enquiretype,setenquiretype]=useState("");
  const[mobileno,setmobileno]=useState("");
  const[successmessage,setsuccessmessage]=useState("");

  const scriptURL = 'https://script.google.com/macros/s/AKfycbxgC8cYuPiwnvPn5ztEGX58wFTcQANEH3Jn-OBNuXCJOGNH3N6PrMqrfNcZZuzyRNCn/exec'
  const form = document.forms['submit-to-google-sheet']

  const updatedata=( e => {
    e.preventDefault()
    fetch(scriptURL, { method: 'POST', body: new FormData(form)})
      .then(response => setsuccessmessage("We will get back to you"))
      .catch(error => console.error('Error!', error.message))
  })
const numberValidation =(e)=>{
  {e.length?10:setmobileno(e)}
}


  return (
    <div className='container'>
      <div>
        <div>
            <h2>Get in Touch with Us</h2>
            <h3>At DeepValley, we're here to help. Whether you're interested in our products, services, or just have a question, we're always ready to assist you.</h3>
            <p>You can reach us at +91-875-797-7109, or by email at connect@deepvalley.in. Our office is open from 10AM-5PM. If you prefer, you can also use the contact form below to send us a message.</p>
            <div>
              <h3>Address</h3>
              <p>Chandi Road, Near Bahadurpur, Harnaut, Nalanda, Bihar 803110. </p>
            </div>
            <div>
              <h3>Email</h3>
              <a href='connect@deepvalley.in'>connect@deepvalley.in</a>
            </div>
        </div>
        {!successmessage?(<form className='dataform' name='submit-to-google-sheet'>
          <h2>Send us a message</h2>
          <label>Name</label><input type="text" placeholder='Your Name' name='Name' onChange={(e)=>{
            setname(e.target.value)
          }}></input>
          <label>Phone number</label><input type="number" placeholder='Phone Number' name='ph_no' value={mobileno} onChange={(e)=>{
            numberValidation(e.target.value)
          }}></input>
          <label>Email</label><input type="email" placeholder='Email' name='email' onChange={(e)=>{
            setenquiretype(e.target.value)
          }}></input>
          <button onClick={updatedata} name="submit">submit</button>
        </form>):<div className='success_message'>{successmessage}</div>}
        
      </div>
    </div>
  )
}

export default ContactUs
